                    p5.rotate(-1.5708)
                  }
                  if(radianprogress<=-0.1){
                    p5.translate(0,-160);
                    p5.rotate(1.5708)
                  }
                  p5.textSize(23);
                  p5.strokeWeight(2)
                  p5.stroke(0)
                  p5.fill(255);
                  p5.text(group.split(': ')[1], 10, 5)
