import React, {useRef,useState,useEffect} from "react";
import { ReactP5Wrapper } from "react-p5-wrapper";
let first=false
function Tree(props) {
  const [organisms,setOrganisms] = useState([]);
  const [taxonomyTree,setTaxonomyTree] = useState('');

useEffect(()=>{
  fetch('https://api.allorigins.win/get?url=https://api.gbif.org/v1/species?limit=1000')
    .then(response => response.json())
    .then(response =>JSON.parse(response.contents))
    .then(response =>setOrganisms(response.results))
    .catch(err => console.error(err));
},[])

useEffect(()=>{
console.log(taxonomyTree)
},[taxonomyTree])

async function narrowOptions(term){
  first=true
  console.log("narrowing options!!!!!",term.target.value)
  let highertaxonkey=await fetch(`https://api.allorigins.win/get?url=https://api.gbif.org/v1/species/match?name=${term.target.value}`)
    .then(response => response.json())
    .then(response =>JSON.parse(response.contents))
    .then(response =>{
      console.log(response);
      return response.usageKey})
    .catch(err => console.error(err));
    console.log(highertaxonkey)
    await fetch(`https://api.allorigins.win/get?url=https://api.gbif.org/v1/species/search?limit=100%26highertaxon_key=${highertaxonkey}`  )
      .then(response => response.json())
      .then(response =>JSON.parse(response.contents))
      .then(response =>{
        console.log(response.results);
        setOrganisms(response.results);
      }).catch(err => console.error(err));
}


//kingdom,phylum,class,order,family,genus,species
useEffect(()=>{
  let taxonomy={}
  for (let animal of organisms){
    console.log(animal,taxonomy)
    if(first==false){
      if(animal.kingdom){
        if(!Object.keys(taxonomy).includes(animal.kingdom)){
          taxonomy[`${animal.kingdom}`]={}
        }
      }
    }


      if (animal.kingdom&&animal.phylum){
          if(taxonomy[`${animal.kingdom}`]){
            if(!Object.keys(taxonomy[`${animal.kingdom}`]).includes(animal.phylum)){
              taxonomy[`${animal.kingdom}`][`${animal.phylum}`]={}
            }
          }
        }


      if(animal.phylum&animal.class){
            if(!Object.keys(taxonomy[`${animal.phylum}`]).includes(animal.class)){
              taxonomy[`${animal.phylum}`][`${animal.class}`]={}
            }
        }


      if(animal.kingdom&&animal.phylum&&animal.class&&animal.order){
        if(taxonomy[`${animal.kingdom}`][`${animal.phylum}`][`${animal.class}`]){
          if(!Object.keys(taxonomy[`${animal.kingdom}`][`${animal.phylum}`][`${animal.class}`]).includes(animal.order)){
            taxonomy[`${animal.kingdom}`][`${animal.phylum}`][`${animal.class}`][`${animal.order}`]={}
          }
        }
      }


      if(animal.kingdom&&animal.phylum&&animal.class&&animal.order&&animal.family){
      if(taxonomy[`${animal.kingdom}`][`${animal.phylum}`][`${animal.class}`][`${animal.order}`]){
        if(!Object.keys(taxonomy[`${animal.kingdom}`][`${animal.phylum}`][`${animal.class}`][`${animal.order}`]).includes(animal.family)){
          taxonomy[`${animal.kingdom}`][`${animal.phylum}`][`${animal.class}`][`${animal.order}`][`${animal.family}`]={}
        }
      }
    }

      if(animal.kingdom&&animal.phylum&&animal.class&&animal.order&&animal.family&&animal.genus){
      if(taxonomy[`${animal.kingdom}`][`${animal.phylum}`][`${animal.class}`][`${animal.order}`][`${animal.family}`]){
        if(!Object.keys(taxonomy[`${animal.kingdom}`][`${animal.phylum}`][`${animal.class}`][`${animal.order}`][`${animal.family}`]).includes(animal.genus)){
          taxonomy[`${animal.kingdom}`][`${animal.phylum}`][`${animal.class}`][`${animal.order}`][`${animal.family}`][`${animal.genus}`]={}
        }
      }
    }
    if(animal.kingdom&&animal.phylum&&animal.class&&animal.order&&animal.family&&animal.genus&&animal.species){
      if(taxonomy[`${animal.kingdom}`][`${animal.phylum}`][`${animal.class}`][`${animal.order}`][`${animal.family}`][`${animal.genus}`]){
        if(!Object.keys(taxonomy[`${animal.kingdom}`][`${animal.phylum}`][`${animal.class}`][`${animal.order}`][`${animal.family}`][`${animal.genus}`]).includes(animal.species)){
          taxonomy[`${animal.kingdom}`][`${animal.phylum}`][`${animal.class}`][`${animal.order}`][`${animal.family}`][`${animal.genus}`][`${animal.species}`]={}
        }
      }
    }

  }
  console.log(taxonomy)
  setTaxonomyTree(JSON.stringify(taxonomy))
},[organisms])


  const sketch = (p5) => {
		let theta,input,button
    let numberofgroups=0

    p5.setup = () => {
			p5.createCanvas(1000, 1000);
		   p5.background(p5.color(255,255,255));
		   p5.stroke(255);
		   p5.strokeWeight(40)

		   // Let's pick an angle 0 to 90 degrees based on the mouse position
		   let a = 15
		   // Convert it to radians
		   theta = p5.radians(a);
		   // Start the tree from the bottom of the screen
		   p5.translate(p5.width/2,p5.height);
		   // Draw a line 120 pixels

		   p5.stroke(101, 67, 33)
		   p5.line(0,0,0,-200);



			 (async function(){
         p5.translate(0,-200);
         p5.rotate(-1.5708)
         let taxonomy=JSON.parse(taxonomyTree)
         delete taxonomy[`incertae sedis`]
         let keys=Object.keys(taxonomy)
         keys=keys.sort(() => Math.random() - 0.5);

         keys=keys.slice(0,6)
         let radiangapbetweenbranches=(180/keys.length) * (Math.PI / 180);
         p5.rotate(radiangapbetweenbranches/2)
         let radianprogress=-1.5708

         for (let [index, group] of keys.entries()){
           p5.push()
           p5.rotate(1.5708)
           p5.rotate(radianprogress)
           p5.stroke(101, 67, 33)
           p5.strokeWeight(30)
    		   p5.line(0,0,0,-200)
           p5.push()
           if(radianprogress>-0.1){
             p5.translate(0,-90);
             p5.rotate(-1.5708)
           }
           if(radianprogress<=-0.1){
             p5.translate(0,-180);
             p5.rotate(1.5708)
           }
          p5.textSize(23);
          p5.strokeWeight(2)
          p5.stroke(0)
          p5.fill(255);
          p5.text(group, 10, 5)
          p5.pop()
          p5.translate(0,-200);
          radianprogress=radianprogress+radiangapbetweenbranches
          branch(taxonomy[`${group}`],160,radianprogress,group)
          p5.pop()

         }
		  })()
		};

  		async function branch(group,size,radianprog,gr){
        p5.rotate(-1.5708)
        let radianprogres=radianprog
        radianprogres=radianprogres-1.5708
        let key=Object.keys(group)
        if(key.length>6){
          key=key.sort(() => Math.random() - 0.5);
          key=key.slice(0,6)
        }
        let radiangapbetweenbranches=(180/key.length) * (Math.PI / 180);
        p5.rotate(radiangapbetweenbranches/2)
        for (let [index, grou] of key.entries()){
          p5.push()
          p5.stroke(101, 67, 33)
          p5.strokeWeight(size/8)
          p5.line(0,0,0,-size)
          p5.push()
          p5.stroke(0,128,0)
          let x=Math.random()
          if(x<0.5){
            p5.ellipse(size/4, 0, size/4, size/16);
            p5.ellipse(-size/4, -size/2, size/4, size/16);
            p5.ellipse(size/4, -size, size/4, size/16);
          }else{
            p5.ellipse(-size/4, 0, size/4, size/16);
            p5.ellipse(size/4, -size/2, size/4, size/16);
            p5.ellipse(-size/4, -size, size/4, size/16);
          }
          p5.ellipse(0, -size, size/16,size/4);


          p5.pop()
          p5.push()
          if(radianprogres>-0){
            p5.translate(0,-size/10);
            p5.rotate(-1.5708)
          }else{
            p5.translate(0,-size);
            p5.rotate(1.5708)
          }
         p5.textSize(size/10);
         p5.strokeWeight(2)
         p5.stroke(0)
         p5.fill(255);
         p5.text(grou, 10, 5)
         p5.pop()

         p5.pop()
         radianprogres=radianprogres+radiangapbetweenbranches
         p5.rotate(radiangapbetweenbranches)
  		}
    };



  };
  let taxonom
  let keys
  if(taxonomyTree){
    taxonom=JSON.parse(taxonomyTree)
    keys=Object.keys(taxonom)
  }

  let colors=["green","red","purple","blue","orange","yellow","pink","green","red","purple","blue","orange","yellow","pink"]

  return (<>
    <div style={{display:"flex",width:"100vw"}}>
    {keys&&keys.map((key,index)=><div><button style={{backgroundColor:colors[index]}} onClick={narrowOptions} value={key}>{key}</button>
    {taxonom[`${key}`]&&Object.keys(taxonom[`${key}`]).map(item=><button style={{backgroundColor:colors[index]}}  onClick={narrowOptions} value={item}>{item}</button>)}
    </div>)}
    </div>
    <ReactP5Wrapper sketch={sketch} /></>)
}
const areEqual = (prevProps, nextProps) => true;

export default React.memo(Tree, areEqual);
